import React from 'react';
import { Formik } from 'formik'

const AdicionaCliente = () => {




  return (
    <>
      <h1>Cadastro de Clientes</h1>

      <Formik 
      initialValues={{nome: '', email: '', nascimento: ''}}
      validate={(values) => {
        const erros = {};
        if (!values.nome){
          erros.nome = 'O Nome precisa ser preenchido'
        }
        if (!values.email){
          erros.email = 'O Email precisa ser preenchido'
        }else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
          errors.email = 'Invalid email address';}


        if (!values.nascimento){
          erros.nascimento = 'O Nascimento precisa ser preenchido'
        }
        return erros;



      }}
      onSubmit={(values) => {
        alert(JSON.stringify(values));
      }}
      >
        {(props) => (

          <form onSubmit={props.handleSubmit} noValidate>
          <div className="form-group">
            <label htmlFor="nome">Nome</label>
            <input 
            id="nome" 
            name="nome" 
            type="text" 
            value={props.values.nome}
            onChange={props.handleChange} 
            onBlur={props.handleBlur}
            />

            {props.errors.nome && props.touched.nome ? 
            <div className="invalid-feedback"> {props.errors.nome} </div> : null }            


          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input 
            id="email" 
            name="email" 
            type="email" 
            value={props.values.email}
            onChange={props.handleChange}
            onBlur={props.handleBlur}
            />
            {props.errors.email && props.touched.email? 
            <div className="invalid-feedback"> {props.errors.email} </div> : null }
          </div>
          <div className="form-group">
            <label htmlFor="date">Data de Nascimento</label>
            <input 
            id="nascimento" 
            name="nascimento" 
            type="date"
            value={props.values.nascimento}
            onChange={props.handleChange}
            onBlur={props.handleBlur}
            />
            {props.errors.nascimento && props.touched.nascimento ? 
            <div className="invalid-feedback"> {props.errors.nascimento} </div> : null }

          </div>
          <button type="submit">Adicionar</button>
        </form>
          )}
      </Formik>
    </>
  );
};

export default AdicionaCliente;
