import React from 'react';
import { Formik, Field } from 'formik'

const Campo = props => {

  const [field, meta] = useField(props);
  return (
      <>
      <input 
          {...field}
          {...props}
          className={meta.error && meta.touched ? 'is-invalid': '' }
      />
      {meta.error && meta.touched ? (
          <div className="invalid-feedback"> {meta.error} </div>
      ):null }
      </>
  );
};



const AdicionaCliente = () => {
  return (
    <>
      <h1>Cadastro de Clientes</h1>

      <Formik 
      initialValues={{nome: '', email: '', nascimento: ''}}
      validate={(values) => {
        const errors = {};
        if (!values.nome){
          errors.nome = 'O Nome precisa ser preenchido'
        }
        if (!values.email){
          errors.email = 'O Email precisa ser preenchido'
        }else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
        ) {
          errors.email = 'O email e invalido';
        }


        if (!values.nascimento){
          errors.nascimento = 'O Nascimento precisa ser preenchido'
        }
        return errors;



      }}
      onSubmit={(values) => {
        alert(JSON.stringify(values));
      }}
      >
        {(props) => (

          <form onSubmit={props.handleSubmit} noValidate>
          <div className="form-group">
            <label htmlFor="nome">Nome</label>
            <Campo 
            id="nome" 
            name="nome" 
            type="text" 
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <Campo 
            id="email" 
            name="email" 
            type="email" 
            />
          </div>
          <div className="form-group">
            <label htmlFor="date">Data de Nascimento</label>
            <Campo 
            id="nascimento" 
            name="nascimento" 
            type="date"
            className = {props.errors.nascimento && props.touched.nascimento ? 'is-invalid' : ''}

            />
            {props.errors.nascimento && props.touched.nascimento ? 
            <div className="invalid-feedback"> {props.errors.nascimento} </div> : null }

          </div>
          <button type="submit">Adicionar</button>
        </form>
          )}
      </Formik>
    </>
  );
};

export default AdicionaCliente;
